import React from "react";

class Login extends React.Component {
      constructor(props) {
          super(props);
          this.state = {
              username: '',
              password: '',
              loggingIn: false,
              submitted: false,
              error_message: null,
          };
          this.handleChange = this.handleChange.bind(this)
          this.handleSubmit = this.handleSubmit.bind(this)
      }

      handleChange(e) {
            const { name, value } = e.target;
            this.setState({ [name]: value });
      }
	  
      render() {
            console.log("render");
            let { submitted, username, password, loggingIn } = this.state;
            return (
                <div className="col-md-6 me-0">
                    {this.state.error_message && <div className="alert alert-danger mt-1 me-0 ms-0">
                        {this.state.error_message}
                    </div>}
                <h2>Вход</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Логин</label>
                        <input type="text" className={'form-control' + (submitted && !username ? ' is-invalid' : '' )}
                                name="username" value={username}
                                onChange={this.handleChange} />
                        {submitted && !username && <div className="help-block text-danger">Введите имя пользователя</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Пароль</label>
                        <input type="password" className={'form-control' + (submitted && !password ? ' is-invalid' : '' )}
                                name="password" value={password}
                                onChange={this.handleChange} />
                        {submitted && !password && <div className="help-block text-danger">Введите пароль</div>}
                    </div>
                    <div className="form-group mt-2">
                        <button className="btn btn-primary">
                            {loggingIn && <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"
                                onSubmit={this.handleSubmit}></span>}
                            Вход
                        </button>
                    </div>
                </form>
                </div>
            );
      }
}

export default Login;
